import { useState } from "react";


const User = () => {
    // defining states
    const [name, setName] = useState("");
    const [address, setAddress] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [isLoggedin, setIsLoggedin] = useState(false);
    const [alert, setAlert] = useState("");

    //tab function
    function showForm(formName) {
        var i, formContent, tab;
        formContent = document.getElementsByClassName("form-content");
        for (i = 0; i < formContent.length; i++) {
            formContent[i].style.display = "none";
        }
        tab = document.getElementsByClassName("tab");
        for (i = 0; i < tab.length; i++) {
            tab[i].className = tab[i].className.replace("active", "");
        }
        document.getElementById(formName + "-form").style.display = "block";
        event.currentTarget.className += "active";
    }

    // login fucntion
    const login = (e) => {
        e.preventDefault();
        setIsLoggedin(true);


    }

    //logout function
    const logout = () => {
        setIsLoggedin(false)
    }

    return (<>
        {!isLoggedin ? (
            <div class="container">
                <div class="description-container">
                    <div class="title">
                        <h1>BLOCKCHAIN <span>based</span></h1>
                        <h2>E-Voting System</h2>
                    </div>
                    <div class="content">
                        <p>we believe that voting is a fundamental right and that everyone should have access to a secure and
                            transparent voting system. That's why we've created a blockchain-based e-voting system that ensures
                            the integrity and security of every vote.
                        </p>
                        <p>
                            Our e-voting system is powered by blockchain technology, which means that every vote is recorded and
                            stored in a tamper-proof and transparent ledger. This makes our e-voting system the most secure and
                            reliable.</p>
                    </div>
                </div>
                <div class="form-container">
                    <div class="form-tabs">
                        <button class="tab active" onclick={showForm('login')}>Login</button>
                        <button class="tab " onclick={showForm('register')}>Register</button>
                    </div>
                    <div id="login-form" class="form-content active">
                        <h2>Login</h2>
                        <form action="POST">
                            <label for="email">Email:</label>
                            <input type="email" id="email" name="email" placeholder="Enter your email address" required />
                            <label for="password">Password:</label>
                            <input type="password" id="password" name="password" placeholder="Enter your password" required />
                            <button type="submit">Login</button>
                        </form>
                    </div>
                    <div id="register-form" class="form-content">
                        <h2>Register</h2>
                        <form action="POST">
                            <label for="full-name">Full Name:</label>
                            <input type="text" id="full-name" name="full-name" placeholder="Enter your full name" required />
                            <label for="address">Address:</label>
                            <input type="text" id="address" name="address" placeholder="Enter your address" required />
                            <label for="email">Email:</label>
                            <input type="email" id="email" name="email" placeholder="Enter your email address" required />
                            <label for="password">Password:</label>
                            <input type="password" id="password" name="password" placeholder="Enter your password" required />
                            <button type="submit">Register</button>
                        </form>
                    </div>
                </div>
            </div>
        ) : (
            <div class="vote-container">
                <div class="voter-info-container">
                    <span class="head">Voter Information</span>
                    <span class="name">Name = Sandesh Bhusal</span>
                    <span class="address">Address = Butwal</span>
                    <span class="email">Email = bhusalsandesh0@gmail.com</span>
                    <span class="voter-id">Voter Id = xxxxxx</span>
                </div>
                <button type="submit">Logout</button>

                <div class="vote-form-container">
                    <h2>Please Select the Candidates and Enter Voter Id </h2>
                    <form>
                        <label for="category">President:</label>
                        <select name="category" id="category">
                            <option value="null" hidden selected>Select Candidate</option>
                            <option value="President">Anil Shrestha</option>
                            <option value="President">Anil Shrestha</option>
                            <option value="Mayor">Anil Shrestha</option>
                        </select>
                        <label for="category">Vice-President:</label>
                        <select name="category" id="category">
                            <option value="null" hidden selected>Select Candidate</option>
                            <option value="President">Yubraj Bashyal</option>
                            <option value="President">Yubraj Bashyal</option>
                            <option value="Yubraj Bashyal">Yubraj Bashyal</option>
                        </select>
                        <label for="voter-id">Voter Id:</label>
                        <input type="number" id="voter-id" name="voter-id" placeholder="Enter your voter id" required />
                        <button type="submit">Vote</button>
                    </form>
                    <div class="alert">You Have Successfully Voted</div>
                </div>
            </div>
        )}

    </>)

}

export default User;
