import { useState } from "react";
import ContainerDescription from "./ContainerDescription";

const User = () => {
  // defining states
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [dob, setDob] = useState("");
  const [voterId, setVoterId] = useState("");
  const [isLoggedin, setIsLoggedin] = useState(false);
  const [activeTab, setActiveTab] = useState("login");

  function showForm(formName) {
    setActiveTab(formName);
  }

  //tab function

  // login fucntion
  const login = (e) => {
    e.preventDefault();
    setIsLoggedin(true);
  };

  //   register function
  const register = (e) => {
    e.preventDefault();
    setName("");
    setEmail("");
    setAddress("");
    setDob("");
    setPassword("");
  };
  //logout function
  const logout = () => {
    e.preventDefault();
    setIsLoggedin(false);
    setName("");
    setEmail("");
    setAddress("");
    setDob("");
    setPassword("");
    setVoterId("");
  };

  return (
    <>
      {!isLoggedin ? (
        <div class="container">
          <ContainerDescription />

          <div class="form-container">
            {/* switching tabs */}

            <div class="form-tabs">
              <button
                className={`tab ${activeTab === "login" ? "active" : ""}`}
                onClick={() => showForm("login")}
              >
                Login
              </button>
              <button
                className={`tab ${activeTab === "register" ? "active" : ""}`}
                onClick={() => showForm("register")}
              >
                Register
              </button>{" "}
            </div>
            {/* login form */}

            <div
              id="login-form"
              className={`form-content ${
                activeTab === "login" ? "active" : ""
              }`}
            >
              <h2>Login</h2>
              <form action="POST">
                <label for="email">Email:</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  placeholder="Enter your email address"
                  onChange={(e) => setEmail(e.target.value)}
                  value={email}
                  required
                />
                <label for="password">Password:</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  placeholder="Enter your password"
                  onChange={(e) => setPassword(e.target.value)}
                  value={password}
                  required
                />
                <button type="submit">Login</button>
              </form>
            </div>
            <div
              id="register-form"
              className={`form-content ${
                activeTab === "register" ? "active" : ""
              }`}
            >
              <h2>Register</h2>
              <form action="POST">
                <label for="full-name">Full Name:</label>
                <input
                  type="text"
                  id="full-name"
                  name="full-name"
                  placeholder="Enter your full name"
                  onChange={(e) => setName(e.target.value)}
                  value={name}
                  required
                />
                <label for="address">Address:</label>
                <input
                  type="text"
                  id="address"
                  name="address"
                  placeholder="Enter your address"
                  onChange={(e) => setAddress(e.target.value)}
                  value={address}
                  required
                />
                <label for="email">Email:</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  placeholder="Enter your email address"
                  onChange={(e) => setEmail(e.target.value)}
                  value={email}
                  required
                />

                <label for="dob">Date of Birth:</label>
                <input
                  type="date"
                  id="dob"
                  name="dob"
                  placeholder="Enter your date of birth"
                  onChange={(e) => setDob(e.target.value)}
                  value={dob}
                  required
                />

                <label for="password">Password:</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  placeholder="Enter your password"
                  onChange={(e) => setPassword(e.target.value)}
                  value={password}
                  required
                />
                <button type="submit">Register</button>
              </form>
            </div>
          </div>
        </div>
      ) : (
        <div class="vote-container">
          <div class="voter-info-container">
            <span class="head">Voter Information</span>
            <span class="name">Name = Sandesh Bhusal</span>
            <span class="address">Address = Butwal</span>
            <span class="email">Email = bhusalsandesh0@gmail.com</span>
            <span class="voter-id">Voter Id = xxxxxx</span>
          </div>

          <button type="submit">Logout</button>

          <div class="vote-form-container">
            <h2>Please Select the Candidates and Enter Voter Id </h2>
            <form>
              <label for="category">President:</label>
              <select name="category" id="category">
                <option value="null" hidden selected>
                  Select Candidate
                </option>
                <option value="President">Anil Shrestha</option>
                <option value="President">Anil Shrestha</option>
                <option value="Mayor">Anil Shrestha</option>
              </select>
              <label for="category">Vice-President:</label>
              <select name="category" id="category">
                <option value="null" hidden selected>
                  Select Candidate
                </option>
                <option value="President">Yubraj Bashyal</option>
                <option value="President">Yubraj Bashyal</option>
                <option value="Yubraj Bashyal">Yubraj Bashyal</option>
              </select>
              <label for="voter-id">Voter Id:</label>
              <input
                type="number"
                id="voter-id"
                name="voter-id"
                placeholder="Enter your voter id"
                onChange={(e) => setVoterId(e.target.value)}
                value={voterId}
                required
              />
              <button type="submit">Vote</button>
            </form>
            <div class="alert">You Have Successfully Voted</div>
          </div>
        </div>
      )}
    </>
  );
};

export default User;
