    // defining states
    const [name, setName] = useState("");
    const [address, setAddress] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [isLoggedin, setIsLoggedin] = useState(false);

    // login fucntion
    const login = (e) => {
        e.preventDefault();
        setIsLoggedin(true);


    }

    //logout function
    const logout = () => {
        setIsLoggedin(false)
    }

    return (<>
        {!isLoggedin ? (
            <div class="container">
                <div class="description-container">
                    <div class="title">
                        <h1>BLOCKCHAIN <span>based</span></h1>
                        <h2>E-Voting System</h2>
                    </div>
                    <div class="content">
                        <p>we believe that voting is a fundamental right and that everyone should have access to a secure and
                            transparent voting system. That's why we've created a blockchain-based e-voting system that ensures
                            the integrity and security of every vote.
                        </p>
                        <p>
                            Our e-voting system is powered by blockchain technology, which means that every vote is recorded and
                            stored in a tamper-proof and transparent ledger. This makes our e-voting system the most secure and
                            reliable.</p>
                    </div>
                </div>

            </div>

        ) : (
            <>
                <h1>User is logged in</h1>
                <button onClickCapture={logout}>logout user</button>
            </>
        )}

    </>)

}

export default User;
