import axios from "axios";
const baseurl = "http://localhost:3001";

// admin login
export const adminLogin = async (username, password) => {
  return await axios({
    method: "POST",
    url: baseurl + "/admin-login",
    data: {
      username: username,
      password: password,
    },
  }).then((response) => {
    return response.data;
  });
};

// create admin
export const createAdmin = async (username, password, token) => {
  console.log(token);
  return await axios({
    method: "POST",
    url: baseurl + "/create-admin",
    data: {
      username: username,
      password: password,
    },
    headers: {
      Authorization: `Bearer ${token}`,
    },
  }).then((response) => {
    return response.data;
  });
};

// create party
export const createParty = async (name, token) => {
  return await axios({
    method: "POST",
    url: baseurl + "/create-party",
    data: {
      name: name,
    },
    headers: {
      Authorization: `Bearer ${token}`,
    },
  }).then((response) => {
    return response.data;
  });
};

// get party
export const getParty = async (token) => {
  return await axios({
    method: "GET",
    url: baseurl + "/get-party",
    headers: {
      Authorization: `Bearer ${token}`,
    },
    responseType: "json",
  });
};

// create category
export const createCategory = async (name, token) => {
  return await axios({
    method: "POST",
    url: baseurl + "/create-category",
    data: {
      name: name,
    },
    headers: {
      Authorization: `Bearer ${token}`,
    },
  }).then((response) => {
    return response.data;
  });
};

// create candidate

export const createCandidate = async (
  name,
  address,
  citizenshipid,
  dob,
  c_id,
  p_id,
  token
) => {
  return await axios({
    method: "POST",
    url: baseurl + "/create-candidate",
    data: {
      name: name,
      address: address,
      citizenshipid: citizenshipid,
      dob: dob,
      c_id: c_id,
      p_id: p_id,
    },
    headers: {
      Authorization: `Bearer ${token}`,
    },
  }).then((response) => {
    return response.data;
  });
};
