import { useState, useEffect } from "react";
import ContainerDescription from "./ContainerDescription";
import Login from "./UserManagement/Login";
import Register from "./UserManagement/Register";
import Vote from "./Vote/Vote";

const User = () => {
  // defining states
  const [isLoggedin, setIsLoggedin] = useState(false);
  const [activeTab, setActiveTab] = useState("login");
  const [token, setToken] = useState("");
  const [data, setData] = useState([]);

  useEffect(() => {
    // Retrieve token from session storage if available
    const storedToken = sessionStorage.getItem("token");
    if (storedToken) {
      setToken(storedToken);
      setIsLoggedin(true);
    }
  }, []);

  //   tab function
  const showForm = (formName) => {
    setActiveTab(formName);
  };

  // loggedin State Handler Function
  const loginStateHandler = (state, token, data) => {
    setIsLoggedin(state);
    setToken(token);
    setData(data);
    // Store token in session storage
    sessionStorage.setItem("token", token);
  };

  // logout function
  const logoutHandler = () => {
    setIsLoggedin(false);
    setToken("");
    setData([]);
    // Remove token from session storage
    sessionStorage.removeItem("token");
  };

  return (
    <>
      {!isLoggedin ? (
        <div class="container">
          <ContainerDescription />

          <div class="form-container">
            {/* switching tabs */}

            <div class="form-tabs">
              <button
                className={`tab ${activeTab === "login" ? "active" : ""}`}
                onClick={() => showForm("login")}
              >
                Login
              </button>
              <button
                className={`tab ${activeTab === "register" ? "active" : ""}`}
                onClick={() => showForm("register")}
              >
                Register
              </button>{" "}
            </div>

            <div
              id="login-form"
              className={`form-content ${
                activeTab === "login" ? "active" : ""
              }`}
            >
              <h2>Login</h2>
              <Login loginState={loginStateHandler} />
            </div>
            <div
              id="register-form"
              className={`form-content ${
                activeTab === "register" ? "active" : ""
              }`}
            >
              <h2>Register</h2>
              <Register />
            </div>
          </div>
        </div>
      ) : (
        <Vote token={token} data={data} logoutHandler={logoutHandler} />
      )}
    </>
  );
};

export default User;
